mapper.select:
SELECT * FROM usage_record ur


        AND ur.usage_time >= #{startTime, jdbcType=TIMESTAMP}


        AND ur.usage_time <= #{endTime, jdbcType=TIMESTAMP}


ORDER BY ur.usage_time DESC

mapper.update:
UPDATE usage_record
amount = #{amount}, unit = #{unit}, usage_time = #{usageTime}, usage_purpose = #{usagePurpose}, notes = #{notes}
WHERE id = #{id}

mapper.select:
SELECT
FROM usage_record ur LEFT JOIN chemical c ON ur.chemical_id = c.id LEFT JOIN man e ON ur.user_id = e.id
AND ur.id = #{id}

SELECT SUM(amount)
FROM usage_record ur
LEFT JOIN chemical c ON ur.chemical_id = c.id


        AND c.name LIKE CONCAT('%', #{chemicalName, jdbcType=VARCHAR}, '%')


        AND ur.usage_time >= #{startTime, jdbcType=TIMESTAMP}


        AND ur.usage_time <= #{endTime, jdbcType=TIMESTAMP}

SELECT IFNULL(SUM(amount), 0)
FROM usage_record ur
LEFT JOIN chemical c ON ur.chemical_id = c.id


        AND c.name LIKE CONCAT('%', #{chemicalName, jdbcType=VARCHAR}, '%')


        AND ur.usage_time >= #{startTime, jdbcType=TIMESTAMP}


        AND ur.usage_time <= #{endTime, jdbcType=TIMESTAMP}
